(4057, '000400000015f600', 'CTR-N-KXBA', 'Code Name: S.T.E.A.M. Demo', 'USA', 244087604, 'eshop', '50010000028835', 'https://kanzashi-ctr.cdn.nintendo.net/i/6b054a7c8e6693f01364548ea827106850b23fa7eed179962f6b07e54bfa8664.jpg', 'none', 'none', -1, 'http://i.imgur.com/2mXB9Af.jpg', 'none', 0, '2016-07-11 20:08:47'),
(4058, '00040002000f2c01', 'CTR-T-JAJE', 'Brunch Panic(Demo)', 'USA', 70983680, 'demo', '50030000003065', 'none', 'none', 'none', -1, 'none', 'none', 0, '2016-07-11 20:08:47'),
(4059, '00040002000c5901', 'CTR-T-JF4E', 'Fishdom H2O: Demo', 'USA', 34312192, 'demo', '50030000005106', 'none', 'none', 'none', -1, 'none', 'none', 0, '2016-07-11 20:08:47'),
(4060, '00040000000afa00', 'CTR-N-JPXE', 'PIX3D', 'USA', 21494628, 'eshop', '50010000012747', 'https://kanzashi-ctr.cdn.nintendo.net/i/a6836040418c87e3d5199e0ebb8101b476fa04d314ae1478d5d8c7c52682a47c.jpg', 'One thousand levels, 3 different game modes. Battle against time and get the highest score in the greatly-addictive pix puzzle game – PIX3D.\n<br><br>\nImagine a picture. A simple picture. A pix art picture.<br><br>\nNow break it down to every single pixel. That''s a colorful mess, huh?<br><br>\nSo PIX3D is about fixing that mess to reproduce a beautiful pixel art picture. It''s challenging, addictive and it piques your curiosity.<br><br>\nWith finely-tuned controls, using the stylus or Circle Pad, simple but amazing graphics and retro 8-bit music, the game introduces us to its unique world.<br><br>\nEnjoy three different modes, fight against time and show the world how PIX3D you are!', 'none', 3, 'http://i.imgur.com/xUzaaBG.jpg', 'none', 0, '2016-07-11 20:08:47'),
